import React, { useState } from "react";

function GovtIssues() {
  // Initial states for upvotes, downvotes, and comments
  const [upvotes, setUpvotes] = useState([0, 0, 0]);
  const [downvotes, setDownvotes] = useState([0, 0, 0]);
  const [comments, setComments] = useState([[], [], []]); // Array of arrays for comments
  const [isCommentBoxOpen, setIsCommentBoxOpen] = useState([false, false, false]); // Track which dropdown is open
  const [currentIssueIndex, setCurrentIssueIndex] = useState(null);
  const [commentText, setCommentText] = useState("");

  // Handlers for upvotes and downvotes
  const handleUpvote = (index) => {
    const newUpvotes = [...upvotes];
    newUpvotes[index] += 1;
    setUpvotes(newUpvotes);
  };

  const handleDownvote = (index) => {
    const newDownvotes = [...downvotes];
    newDownvotes[index] += 1;
    setDownvotes(newDownvotes);
  };

  // Handlers for comments
  const handleToggleCommentBox = (index) => {
    const newCommentBoxState = [...isCommentBoxOpen];
    newCommentBoxState[index] = !newCommentBoxState[index]; // Toggle visibility
    setIsCommentBoxOpen(newCommentBoxState);
    setCurrentIssueIndex(index);
  };

  const handleAddComment = () => {
    if (!commentText.trim()) return;
    const newComments = [...comments];
    newComments[currentIssueIndex].push(commentText);
    setComments(newComments);
    setCommentText(""); // Reset comment box
    setIsCommentBoxOpen([false, false, false]); // Close the dropdown after submitting
  };

  // Random image URLs
  const images = [
    "https://images.pexels.com/photos/1640777/pexels-photo-1640777.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1",
    "https://images.pexels.com/photos/414612/pexels-photo-414612.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1",
    "https://images.pexels.com/photos/1329361/pexels-photo-1329361.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1",
  ];

  return (
    <div className="govt-issues container mx-auto py-6">
      <h1 className="text-3xl font-bold text-center mb-4">Government Issues</h1>
      <div className="actions mb-4 flex justify-between">
        <input
          type="text"
          className="input input-bordered w-1/3"
          placeholder="Search issues..."
          onClick={() => alert("Search functionality coming soon!")}
        />
        <button className="btn btn-outline-secondary">Filter / Sort</button>
      </div>

      <div className="issue-list">
        {[1, 2, 3].map((issue, index) => (
          <div key={index} className="issue-card card mb-4 shadow-xl">
            <div className="card-body flex items-center">
              <img
                src={images[index % images.length]}
                alt={`Issue ${issue}`}
                className="w-36 h-36 object-cover rounded-lg mr-4"
              />
              <div className="content ml-4 flex-1">
                <h4 className="text-xl font-semibold mb-2">Issue Title {issue}</h4>
                <p className="text-sm text-gray-600 mb-3">
                  Issue description: Lorem ipsum dolor sit amet, consectetur adipiscing elit. Vestibulum ante ipsum primis...
                </p>
                <div className="actions flex justify-between items-center">
                  <button
                    className="btn btn-success"
                    onClick={() => handleUpvote(index)}
                  >
                    Upvote Count: {upvotes[index]}
                  </button>
                  <button
                    className="btn btn-error"
                    onClick={() => handleDownvote(index)}
                  >
                    Downvote Count: {downvotes[index]}
                  </button>
                  <button
                    className="btn btn-info"
                    onClick={() => handleToggleCommentBox(index)}
                  >
                    Comments [{comments[index].length}]
                  </button>
                  <button className="btn btn-warning">Location</button>
                </div>

                {/* Comment Box Section */}
                {isCommentBoxOpen[index] && (
                  <div className="mt-4 p-4 bg-base-200 dark:bg-base-300 rounded-lg shadow-lg">
                    <h4 className="text-xl font-semibold mb-2 text-black dark:text-white">
                      Add Comment
                    </h4>
                    <textarea
                      className="textarea textarea-bordered w-full mb-4 dark:bg-base-200 dark:text-white"
                      rows="3"
                      value={commentText}
                      onChange={(e) => setCommentText(e.target.value)}
                      placeholder="Enter your comment..."
                    ></textarea>
                    <div className="flex justify-between">
                      <button
                        className="btn btn-primary dark:btn-primary"
                        onClick={handleAddComment}
                      >
                        Submit
                      </button>
                      <button
                        className="btn btn-secondary dark:btn-secondary"
                        onClick={() => setIsCommentBoxOpen([false, false, false])} // Close all dropdowns
                      >
                        Cancel
                      </button>
                    </div>
                  </div>
                )}

                {/* Existing Comments Section */}
                {comments[index].length > 0 && (
                  <div className="mt-4">
                    <h4 className="text-lg font-semibold text-black dark:text-white">
                      Existing Comments
                    </h4>
                    <ul className="list-disc pl-5 space-y-2">
                      {comments[index].map((comment, idx) => (
                        <li key={idx} className="text-sm text-gray-700 dark:text-gray-300">
                          {comment}
                        </li>
                      ))}
                    </ul>
                  </div>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default GovtIssues;
