import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

function IssueDetails() {
  const { id } = useParams(); // Get issue_id from URL params
  const [issue, setIssue] = useState(null); // State to store issue details
  const [upvotes, setUpvotes] = useState(0); // State to store upvotes
  const [downvotes, setDownvotes] = useState(0); // State to store downvotes
  const [loading, setLoading] = useState(true); // Loading state

  // Fetch issue details when component mounts
  useEffect(() => {
    const fetchIssue = async () => {
      try {
        const response = await axios.get(`http://localhost:1241/govtissuedetails/${id}`);
        const fetchedIssue = response.data;

        setIssue({
          title: fetchedIssue.issue_title,
          description: fetchedIssue.issue_description || "No description provided.",
          status: fetchedIssue.issue_status,
          location: fetchedIssue.issue_location || "Not specified.",
          image: fetchedIssue.issue_image || "https://via.placeholder.com/150",
        });

        setUpvotes(fetchedIssue.issue_likes || 0);
        setDownvotes(fetchedIssue.issue_dislikes || 0);
      } catch (error) {
        console.error("Error fetching issue details:", error.message);
        alert("Failed to fetch issue details. Please try again.");
      } finally {
        setLoading(false); // Stop loading spinner
      }
    };

    fetchIssue();
  }, [id]);

  // Handle upvote or downvote action
  const handleVote = async (voteType) => {
    try {
      const updatedVotes = {
        upvotes: voteType === "upvote" ? upvotes + 1 : upvotes,
        downvotes: voteType === "downvote" ? downvotes + 1 : downvotes,
      };

      // Optimistic UI update
      setUpvotes(updatedVotes.upvotes);
      setDownvotes(updatedVotes.downvotes);

      // Update votes in the backend
      await axios.put(`http://localhost:1241/govt/${id}`, {
        upvotes: updatedVotes.upvotes,
        downvotes: updatedVotes.downvotes,
      });
    } catch (error) {
      console.error("Error updating votes:", error.message);
      alert("Failed to update votes. Please try again.");
    }
  };

  // Render loading state
  if (loading) {
    return <div>Loading issue details...</div>;
  }

  // Render error if issue is not found
  if (!issue) {
    return <div>Issue not found. Please check the issue ID.</div>;
  }

  return (
    <div className="issue-details container mt-5">
      <h1 className="mb-4 text-center">{issue.title}</h1>

      <div className="card mb-4">
        <div className="card-body">
          <img
            src={issue.image}
            alt={issue.title}
            style={{ width: "100%", height: "auto", borderRadius: "10px" }}
          />
          <p className="mt-3">
            <strong>Description:</strong> {issue.description}
          </p>
          <p>
            <strong>Location:</strong> {issue.location}
          </p>
          <p>
            <strong>Status:</strong> {issue.status}
          </p>
        </div>
      </div>

      {/* Vote Section */}
      <div className="vote-section d-flex justify-content-between align-items-center mb-4">
        <button className="btn btn-success" onClick={() => handleVote("upvote")}>
          Upvote ({upvotes})
        </button>
        <button className="btn btn-danger" onClick={() => handleVote("downvote")}>
          Downvote ({downvotes})
        </button>
      </div>
    </div>
  );
}

export default IssueDetails;
