import React, { useState, useEffect } from "react";
import axios from "axios";

function GovtIssues2() {
  const [issues, setIssues] = useState([]);

  // Fetch data from the backend
  useEffect(() => {
    const fetchIssues = async () => {
      try {
        const response = await axios.get("http://localhost:1241/govt");
        setIssues(response.data); // Save the issues to the state
      } catch (error) {
        console.error("Error fetching issues:", error);
      }
    };

    fetchIssues();
  }, []);

  return (
    <div className="govt-issues container mx-auto py-6">
      <h1 className="text-3xl font-bold text-center mb-4">Government Issues</h1>
      <div className="issue-list">
        {issues.map((issue, index) => (
          <div key={issue._id} className="issue-card card mb-4 shadow-xl">
            <div className="card-body flex items-center">
              {/* Use the issue_image URL to display the image */}
              <img
                src={issue.issue_image} // Dynamically use the image URL from the backend
                alt={issue.issue_title}
                className="w-36 h-36 object-cover rounded-lg mr-4"
              />
              <div className="content ml-4 flex-1">
                <h4 className="text-xl font-semibold mb-2">{issue.issue_title}</h4>
                <p className="text-sm text-gray-600 mb-3">
                  {issue.issue_description || "No description available."}
                </p>
                <div className="actions flex justify-between items-center">
                  <button className="btn btn-success">
                    Upvote Count: {issue.issue_likes}
                  </button>
                  <button className="btn btn-error">
                    Downvote Count: {issue.issue_dislikes}
                  </button>
                  <button className="btn btn-info">Comments</button>
                  <button className="btn btn-warning">Location</button>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default GovtIssues2;
