//Radiah
import React, { useState } from "react";

function IssueDetails() {
  const [upvotes, setUpvotes] = useState(50);
  const [downvotes, setDownvotes] = useState(10);
  const [comments, setComments] = useState(["Great initiative!", "Needs urgent attention"]);
  const [isCommentBoxOpen, setIsCommentBoxOpen] = useState(false);
  const [commentText, setCommentText] = useState("");
  const [rating, setRating] = useState(null);

  // Handlers for upvotes and downvotes
  const handleUpvote = () => setUpvotes((prev) => prev + 1);
  const handleDownvote = () => setDownvotes((prev) => prev + 1);

  // Handlers for comments
  const handleAddComment = () => {
    if (!commentText.trim()) return;
    setComments((prev) => [...prev, commentText]);
    setCommentText("");
    setIsCommentBoxOpen(false);
  };

  // Handler for rating
  const handleRatingSubmit = (e) => {
    e.preventDefault();
    if (rating < 1 || rating > 5) {
      alert("Please provide a rating between 1 and 5.");
      return;
    }
    alert(`Thank you for rating: ${rating}/5`);
    setRating(null);
  };

  return (
    <div className="issue-details container mt-5">
      <h1 className="mb-4 text-center">School Infrastructure Issues</h1>

      <div className="card mb-4">
        <div className="card-body">
          <img
            //src="https://images.pexels.com/photos/1640777/pexels-photo-1640777.jpeg"//
            src="https://www.unicef.org/bangladesh/sites/unicef.org.bangladesh/files/styles/hero_desktop/public/UN0743047.jpg.webp"
            alt="Issue Thumbnail"
            style={{ width: "100%", height: "auto", borderRadius: "10px" }}
          />
          <p className="mt-3">
            <strong>Description:</strong> Many schools in rural Bangladesh lack proper infrastructure, which affects the quality of education for students.
          </p>
          <p><strong>Location:</strong> Dhaka, Bangladesh</p>
          <p><strong>Status:</strong> Unsolved</p>
        </div>
      </div>

      {/* Vote Section */}
      <div className="vote-section d-flex justify-content-between align-items-center mb-4">
        <button className="btn btn-success" onClick={handleUpvote}>
          Upvote ({upvotes})
        </button>
        <button className="btn btn-danger" onClick={handleDownvote}>
          Downvote ({downvotes})
        </button>
      </div>

      {/* Comments Section */}
      <div className="comments-section">
        <h3>Comments ({comments.length})</h3>
        <ul className="list-group mb-3">
          {comments.map((comment, index) => (
            <li key={index} className="list-group-item">
              {comment}
            </li>
          ))}
        </ul>
        {isCommentBoxOpen ? (
          <div>
            <textarea
              className="form-control mb-2"
              value={commentText}
              onChange={(e) => setCommentText(e.target.value)}
              placeholder="Write your comment here..."
              rows="3"
            />
            <button className="btn btn-primary mr-2" onClick={handleAddComment}>
              Submit
            </button>
            <button className="btn btn-secondary" onClick={() => setIsCommentBoxOpen(false)}>
              Cancel
            </button>
          </div>
        ) : (
          <button className="btn btn-info" onClick={() => setIsCommentBoxOpen(true)}>
            Add Comment
          </button>
        )}
      </div>

      {/* Rating Section */}
      <div className="rating-section mt-4">
        <h3>Rate This Issue</h3>
        <form onSubmit={handleRatingSubmit}>
          <div className="form-group">
            <label htmlFor="rating">Rating (1-5):</label>
            <input
              type="number"
              id="rating"
              className="form-control"
              value={rating || ""}
              onChange={(e) => setRating(e.target.value)}
              min="1"
              max="5"
              required
            />
          </div>
          <button type="submit" className="btn btn-primary">
            Submit Rating
          </button>
        </form>
      </div>
    </div>
  );
}

export default IssueDetails;
