import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

function Nav() {
  const [theme, setTheme] = useState(localStorage.getItem('theme') || 'light'); // State for current theme

  useEffect(() => {
    // Set initial theme based on localStorage or default to light
    document.documentElement.setAttribute('data-theme', theme);
  }, [theme]); // Only re-run the effect when theme changes

  // Function to toggle the theme
  const toggleTheme = () => {
    const newTheme = theme === 'light' ? 'synthwave' : 'light'; // Toggle theme
    setTheme(newTheme); // Update state
    localStorage.setItem('theme', newTheme); // Save theme in localStorage
  };

  return (
    <div className="navbar bg-base-100">
      {/* Dropdown Menu for mobile */}
      <div className="navbar-start">
        <div className="dropdown">
          <div tabIndex={0} role="button" className="btn btn-ghost btn-circle">
            <svg
              xmlns="http://www.w3.org/2000/svg"
              className="h-5 w-5"
              fill="none"
              viewBox="0 0 24 24"
              stroke="currentColor"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth="2"
                d="M4 6h16M4 12h16M4 18h7"
              />
            </svg>
          </div>
          <ul
            tabIndex={0}
            className="menu menu-sm dropdown-content bg-base-100 rounded-box z-[1] mt-3 w-52 p-2 shadow"
          >
            <li><a>Homepage</a></li>
            <li><a>Portfolio</a></li>
            <li><a>About</a></li>
          </ul>
        </div>
      </div>

      {/* Center Logo */}
      <div className="navbar-center">
        <a className="btn btn-ghost text-xl">daisyUI</a>
      </div>

      {/* Navbar End: Theme Toggle Button, Profile, and Log Out */}
      <div className="navbar-end flex flex-col space-y-4">
        {/* Theme Toggle Button */}
        <button
          className="btn w-full"
          onClick={toggleTheme}
        >
          {theme === 'light' ? 'Dark Mode' : 'White Mode'}
        </button>

        {/* Profile Button */}
        <button className="btn w-full">Profile</button>

        {/* Log Out Button */}
        <button className="btn btn-danger w-full">Log Out</button>
      </div>
    </div>
  );
}

export default Nav;
