import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

function Nav() {
  const [theme, setTheme] = useState(localStorage.getItem('theme') || 'light'); // State for current theme

  useEffect(() => {
    // Set initial theme based on localStorage or default to light
    document.documentElement.setAttribute('data-theme', theme);
  }, [theme]); // Only re-run the effect when theme changes

  // Function to toggle the theme
  const toggleTheme = () => {
    const newTheme = theme === 'light' ? 'synthwave' : 'light'; // Toggle theme
    setTheme(newTheme); // Update state
    localStorage.setItem('theme', newTheme); // Save theme in localStorage
  };

  return (
    <div className="sidebar">
      <div className="logo">
        <h2>LOGO</h2>
      </div>
      <ul className="nav flex-column">
        <li className="nav-item">
          <Link to="/" className="nav-link">Current Issues</Link>
        </li>
        <li className="nav-item">
          <Link to="/govt-issues" className="nav-link">Govt. Issues</Link>
        </li>
        <li className="nav-item">
          <Link to="/tweets" className="nav-link">Solved Issues</Link>
        </li>
        <li className="nav-item">
          <Link to="/reports" className="nav-link">Reports</Link>
        </li>
        <li className="nav-item">
          <Link to="/community-service" className="nav-link">Community Service</Link>
        </li>
        <li className="nav-item">
          <Link to="/contact-us" className="nav-link">Contact Us</Link>
        </li>
      </ul>
      <div className="bottom-options">
        {/* Single theme toggle button */}
        <button
          className="btn w-100 mb-6"
          onClick={toggleTheme}
        >
          {theme === 'light' ? 'Dark Mode' : 'White Mode'}
        </button>

        <button className="btn btn-secondary w-100">Profile</button>
        <button className="btn btn-danger w-100">Log Out</button>
      </div>
    </div>
  );
}

export default Nav;
