import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';

function Nav() {
  useEffect(() => {
    const themeController = document.querySelector('.theme-controller');
    // Set initial theme based on the saved theme in localStorage (if any)
    const savedTheme = localStorage.getItem('theme') || 'light';
    document.documentElement.setAttribute('data-theme', savedTheme);

    // Event listener for theme change
    themeController.addEventListener('change', (e) => {
      const theme = e.target.checked ? 'synthwave' : 'light';
      document.documentElement.setAttribute('data-theme', theme);
      localStorage.setItem('theme', theme); // Save the selected theme in localStorage
    });

    // Cleanup event listener
    return () => {
      themeController.removeEventListener('change', () => {});
    };
  }, []);

  return (
    <div className="sidebar">
      <div className="logo">
        <h2>LOGO</h2>
      </div>
      <ul className="nav flex-column">
        <li className="nav-item">
          <Link to="/" className="nav-link">Current Issues</Link>
        </li>
        <li className="nav-item">
          <Link to="/govt-issues" className="nav-link">Govt. Issues</Link>
        </li>
        <li className="nav-item">
          <Link to="/tweets" className="nav-link">Solved Issues</Link>
        </li>
        <li className="nav-item">
          <Link to="/reports" className="nav-link">Reports</Link>
        </li>
        <li className="nav-item">
          <Link to="/community-service" className="nav-link">Community Service</Link>
        </li>
        <li className="nav-item">
          <Link to="/contact-us" className="nav-link">Contact Us</Link>
        </li>
      </ul>
      <div className="bottom-options">
        {/* Theme toggle buttons */}
        <div className="theme-buttons mb-4">
          <button
            className="btn btn-dark w-100"
            onClick={() => {
              document.documentElement.setAttribute('data-theme', 'synthwave');
              localStorage.setItem('theme', 'synthwave');
            }}
          >
            Dark Mode
          </button>
          <button
            className="btn btn-light w-100 mt-2"
            onClick={() => {
              document.documentElement.setAttribute('data-theme', 'light');
              localStorage.setItem('theme', 'light');
            }}
          >
            White Mode
          </button>
        </div>

        <button className="btn btn-secondary w-100">Profile</button>
        <button className="btn btn-danger w-100">Log Out</button>
      </div>
    </div>
  );
}

export default Nav;
